import React, { useState } from "react";

type Props = {
  iconActive: string;
  iconInactive: string;
  label?: string;
  active?: boolean;
  onClick?: () => void;
};

export const DrawerMenuButton: React.FC<Props> = ({
  iconActive,
  iconInactive,
  label,
  active = false,
  onClick,
}) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <button
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={onClick}
      className={`flex items-center justify-start space-x-4 py-2 px-4 rounded-2xl transition delay-75 ease-in-out ${
        active
          ? "bg-[#8e80d9] bg-opacity-80"
          : "hover:bg-[#8e80d9] hover:bg-opacity-20"
      }`}
    >
      <img
        className="w-6"
        src={active ? iconActive : iconInactive}
        alt="icon"
      />
      {label && (
        <span
          className={`py-1 text-lg text-slate-900 ${active ? "font-bold" : ""}`}
        >
          {label}
        </span>
      )}
    </button>
  );
};
