import React, { useEffect, useState } from "react";

export const RecommendedSection: React.FC = () => {
  const [books, setBooks] = useState<any[]>([]);

  useEffect(() => {
    fetch("http://localhost:3001/api/users")
      .then((res) => res.json())
      .then((data) => setBooks(data));
  }, []);

  return (
    <div className="rounded-xl w-1/2 bg-slate-100 shadow-md h-1/2">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold">Recommandé</h2>
        <button className="bg-[#8e80d9] p-2 rounded-xl text-slate-100">
          Voir plus
        </button>
      </div>
    </div>
  );
};
