import React, { useState } from "react";

export const CategoriesSection: React.FC = () => {
  const [selectedCategory, setSelectedCategory] = useState<string>("Tous");

  return (
    <div className="rounded-xl w-full bg-slate-100 shadow-md h-auto p-4 space-y-4">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold">Catégories</h2>
      </div>
      <div className="space-x-5">
        <button
          className={`${
            selectedCategory === "Tous"
              ? "bg-[#8e80d9] text-slate-100"
              : "bg-[#d2ceea] hover:bg-[#aca1ef]"
          } rounded-lg bg-opacity-80 p-2`}
        >
          <span>Tous</span>
        </button>
        <button
          className={`${
            selectedCategory === "Fiction"
              ? "bg-[#8e80d9] text-slate-100"
              : "bg-[#dbd8ed] hover:bg-[#b4abee] hover:text-slate-100"
          } rounded-lg bg-opacity-80 p-2 ease-in-out transition duration-300`}
        >
          <span>Fiction</span>
        </button>
        <button
          className={`${
            selectedCategory === "Romance"
              ? "bg-[#8e80d9] bg-opacity-80 text-slate-100"
              : ""
          } rounded-lg p-2`}
        >
          <span>Romance</span>
        </button>
        <button className="bg-[#8e80d9] bg-opacity-80 rounded-lg p-2 text-slate-100">
          <span>Biographies</span>
        </button>
        <button className="bg-[#8e80d9] bg-opacity-80 rounded-lg p-2 text-slate-100">
          <span>Poésie</span>
        </button>
        <button className="bg-[#8e80d9] bg-opacity-80 rounded-lg p-2 text-slate-100">
          <span>Bandes dessinées</span>
        </button>
        <button className="bg-[#8e80d9] bg-opacity-80 rounded-lg p-2 text-slate-100">
          <span>Mangas</span>
        </button>
        <button className="bg-[#8e80d9] bg-opacity-80 rounded-lg p-2 text-slate-100">
          <span>Albums illustrés</span>
        </button>
      </div>
    </div>
  );
};
