import React from "react";
import logo from "../assets/img/logo.png";
import { DrawerMenuButton } from "./DrawerMenuButton";
import homeInactiveSvg from "../assets/svg/home-icon-white.svg";
import homeActiveSvg from "../assets/svg/home-icon-white.svg";
import { useLocation } from "react-router-dom";

export const DrawerMenu: React.FC = () => {
  const location = useLocation();
  return (
    <div className="w-1/5 h-screen bg-slate-100 drop-shadow-xl px-6">
      <div className="flex items-center space-x-2 justify-center h-28">
        <img className="w-10" src={logo} alt="Logo" />
        <div className="drop-shadow-sm text-2xl font-bold text-[#8e80d9]">
          <span>Biblio</span>
          <span>Tech</span>
        </div>
      </div>
      <div className="mt-16 flex flex-col justify-center">
        <DrawerMenuButton
          active={location.pathname === "/home"}
          label="Acceuil"
          iconActive={homeActiveSvg}
          iconInactive={homeInactiveSvg}
        />
      </div>
    </div>
  );
};
