import React, { useState } from "react";

type Props = {
  iconActive: string;
  iconInactive: string;
  label?: string;
  active?: boolean;
  onClick?: () => void;
};

export const DrawerMenuButton: React.FC<Props> = ({
  iconActive,
  iconInactive,
  label,
  active = false,
  onClick,
}) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <button
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={onClick}
      className={`${
        active
          ? "bg-[#2557a1] bg-opacity-40"
          : "hover:bg-[#2557a1] hover:bg-opacity-20"
      } flex items-center justify-start space-x-4 py-2 px-4 rounded-2xl transition delay-75 ease-in-out`}
    >
      <img
        className="w-5 h-8 3xl:w-6 3xl:h-9"
        src={active ? iconActive : iconInactive}
        alt="icon"
      />
      {label ? (
        <span>{label}</span>
      ) : label ? (
        <div
          className={`absolute left-8 text-slate-900 w-32 flex justify-center items-center rounded-xl`}
        >
          <span
            className={`w-32 py-1 text-lg ${
              active ? "font-extrabold" : "font-bold"
            }`}
          >
            {label}
          </span>
        </div>
      ) : (
        ""
      )}
    </button>
  );
};
